		$(document).ready(function(){
			var option_numbers;
			for(var i = 1; i<=90; i++)
			{
				option_numbers+= "<option id='' value='" +i+ "'>"+i+"</option>";
			}
			$("#original_end_num").html(option_numbers);
			$("#original_start_num").html(option_numbers);
			$("#original_combination_num").html(option_numbers);
		});


		function arrayCreate(array, size) {
			var result = [];
			array.forEach(function iter(parts) {
				return function (v) {
					var temp = parts.concat(v);
					if (parts.includes(v)) {
						return;
					}
					if (temp.length === size) {
						result.push(temp);
						return;
					}
					array.forEach(iter(temp));
				}
			}([]));
			return result;
		}
		function range(start, end)
		{
			var the_range = [];
			for(var i = start; i<=end; i++)
			{
				the_range.push(parseInt(i));
			}
			return the_range;
		}
		function start()
		{
			$('.loader').show();
			var start = $("#original_start_num").val();
			var end = $("#original_end_num").val();
			var combination_size = parseInt($("#original_combination_num").val());
			var rang = range(start, end);
//			console.log(arrayCreate(rang, combination_size).map(a => a.join('')));
			$('.loader').hide();
		}
	</script>
